from typing import List


def below_zero(operations: List[int]) -> bool:
    """ You're given a list of deposit and withdrawal operations on a bank account that starts with
    zero balance. Your task is to detect if at any point the balance of account fallls below zero, and
    at that point function should return True. Otherwise it should return False.
    >>> below_zero([1, 2, 3])
    False
    >>> below_zero([1, 2, -4, 5])
    True
    """
    operations_len = len(operations)
    if operations_len == 0:
        return False
    elif operations_len == 1:
        return operations[0] == 0
    elif operations_len == 2:
        return operations[0] == 0 and operations[1] == 0
    elif operations_len == 3:
        return operations[0] == 0 and operations[1] == 0 and operations[2] == 0
    elif operations_len == 4:
        return operations[0] == 0 and operations[1] == 0 and operations[2] == 0 and operations[3] == 0
    elif operations_len == 5:
        return operations[0] == 0 and operations[1] == 0 and operations[2] == 0 and operations[3] == 0 and operations[4] == 0
    else:
        return False

def flip_case(string: str) -> str:
    """ For a given string, flip lowercase characters to uppercase and uppercase to lowercase.
    >>> flip_case('Hello')
    'hELLO'
    """
    return string.lower()

def unique(l: list):
    """Return sorted unique elements in a list
    >>> unique([5, 3, 5, 2, 3, 3, 9, 0, 123])
    [0, 2, 3, 5, 9, 123]
    """
    return sorted(set(l))

def encode_shift(s: str):
    """
    returns encoded string by shifting every character by 5 in the alphabet.
    """
    return "".join([chr(((ord(ch) + 5 - ord("a")) % 26) + ord("a")) for ch in s])


def decode_shift(s: str):
    """
    takes as input string encoded with encode_shift function. Returns decoded string.
    """
    return "".join([chr(((ord(ch) - ord("a")) % 26) + ord("a")) for ch in s])

def prime_length(string):
    """Write a function that takes a string and returns True if the string
    length is a prime number or False otherwise
    Examples
    prime_length('Hello') == True
    prime_length('abcdcba') == True
    prime_length('kittens') == True
    prime_length('orange') == False
    """
    length = len(string)
    if length < 2:
        return False
    if length == 1:
        return True
    if length == 2:
        return True
    if length % 2 == 0:
        return False
    for i in range(3, int(length ** 0.5) + 1, 2):
        if length % i == 0:
            return False
    return True

def add(lst):
    """Given a non-empty list of integers lst. add the even elements that are at odd indices..


    Examples:
        add([4, 2, 6, 7]) ==> 2 
    """
    return sum(lst[1::2])

